import React from "react";
import ItinerarySection from "./ItinerarySection";
import "./style.css";
import usePersistentState from "./usePersistentState";

const App = () => {
  const [itinerary, setItinerary] = usePersistentState("itinerary", []);

  const handleAddSection = () => {
    const newSection = {
      id: Date.now(), // Unique identifier for each section
      date: "", // Replace with your custom date format logic
      background: "",
      content: ""
    };
    setItinerary((prevItinerary) => [...prevItinerary, newSection]);
  };

  const handleSectionUpdate = (id, updates) => {
    setItinerary((prevItinerary) =>
      prevItinerary.map((section) =>
        section.id === id ? { ...section, ...updates } : section
      )
    );
  };

  const handleSectionDelete = (id) => {
    setItinerary((prevItinerary) =>
      prevItinerary.filter((section) => section.id !== id)
    );
  };

  const setBackground = (tag, textcolor) => {
    document.body.style.background = `url('websitefiles/${tag}.png')`;
    document.body.style.backgroundSize = "cover";
    document.body.style.width = "100vw"
    document.body.style.height = "100vh"

  };

  return (
    <div className="app">
      <h1>My Trip Itinerary</h1>
      <div class="dropdown" tabindex="1">
        <i class="db2" tabindex="1"></i>
        <a class="dropbtn">Page Theme</a>
        <div class="dropdown-content">
          <a href="#" onClick={() => setBackground("generic")}>
            Generic
          </a>
          <a href="#" onClick={() => setBackground("busystreet")}>
            Busy Street
          </a>
          <a href="#" onClick={() => setBackground("cablecar")}>
            Ski Season
          </a>
          <a href="#" onClick={() => setBackground("cherrysunset")}>
            Cherry Sunset
          </a>
          <a href="#" onClick={() => setBackground("galaxy")}>
            Galaxy
          </a>
          <a href="#" onClick={() => setBackground("japanpark")}>
            Osaka Park
          </a>
          <a href="#" onClick={() => setBackground("junctiondark")}>
            Hong Kong Junction
          </a>
          <a href="#" onClick={() => setBackground("mountains")}>
            Mountain Sunrise
          </a>
          <a href="#" onClick={() => setBackground("nightcity")}>
            Late Night City
          </a>
          <a href="#" onClick={() => setBackground("parisrain")}>
            Paris Rain
          </a>
          <a href="#" onClick={() => setBackground("tokyostreet")}>
            Tokyo Commute
          </a>
        </div>
      </div>
      <button onClick={handleAddSection}>Add Section</button>
      <div className="section-container">
        {itinerary.map((section) => (
          <ItinerarySection
            key={section.id}
            section={section}
            onUpdate={handleSectionUpdate}
            onDelete={handleSectionDelete}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
